mapper.insert:
        insert into unity_user (department_id,username,password,name,phone,email,nick_name,major,grade,class_number,dorm,politicsStatus,avatar)select
#{departmentId},#{username},#{password},#{name}, #{phone}, #{email}, #{nickName}, #{major}, #{grade}, #{classNumber}, #{dorm}, #{politicsStatus}, #{avatar}
            WHERE NOT EXISTS (SELECT * FROM unity_user WHERE username = #{username})
